import React from "react";
import '../App.css';

class Pokemon extends React.Component{


    constructor(props){
        super(props);

        this.state={
            pokemon:null,
            isShiny:false
        }
    }

    componentDidMount(){
        const idPokemon = Math.floor(Math.random() * 250) + 1  

        fetch("https://pokeapi.co/api/v2/pokemon/"+idPokemon)
            .then((resp)=>resp.json())
            .then((data) => this.setPokemon(data));
    }

    setPokemon(pokemon){
        this.setState({pokemon});
    }

    setShiny(v){
        this.setState({isShiny:v});
    }

    getPokemonImage = () => {
        if(this.state.isShiny){
            return this.state.pokemon.sprites.front_shiny;
        }else{
            return this.state.pokemon.sprites.front_default;
        }
    }

    render(){
        return(
            <>
            {
                this.state.pokemon != null &&
                <div className='divPokemon' onMouseOver={()=>this.setShiny(true)} onMouseLeave={()=>this.setShiny(false)}>
                    <img src={this.getPokemonImage()}/>
                    <h3>{this.state.pokemon.name}</h3>
                </div>
            }
            </>
        );
    }
}

export default Pokemon;
