import React, { useEffect, useState } from 'react';

function Login() {

    const [mail, setMail] = useState(""); // Déclaration du state
    const [password, setPassword] = useState("");

    function changeMail(e){ // Modifie la valeur du state par celle entrée dans l'input
        setMail(e.target.value);
    }

    function changePassword(event){ // idem
        setPassword(event.target.value);
    }

    return (
        <div>
            <input onChange={(e)=>changeMail(e)} placeholder="Entrer le mail" value={mail}></input>
            <input onChange={(event)=>changePassword(event)} placeholder="Entrer le mail" value={password}></input>

            {
                mail == "test" && password == "1234" &&
                <p>ok</p>
            }
        </div>
    );
}

export default Login;
