import React from "react";
import '../App.css';

function Pokemon() {

    const[pokemon, setPokemon] = useState(null);
    const[isShiny, setIsShiny] = useState(false);

    useEffect(()=>{ // Equivaut au componentDidMount : s'execute à chaque modification de buttonTitle
        const idPokemon = Math.floor(Math.random() * 250) + 1 
        fetch("https://pokeapi.co/api/v2/pokemon/"+idPokemon)
            .then((resp)=>resp.json())
            .then((data) => this.setPokemon(data));
    },[]);

    function setShiny(v){
        setIsShiny(v);
    }

    function getPokemonImage() {
        if(isShiny){
            return this.state.pokemon.sprites.front_shiny;
        }else{
            return this.state.pokemon.sprites.front_default;
        }
    }

    return(
        <>
        {
            this.state.pokemon != null &&
            <div className='divPokemon' onMouseOver={()=>setShiny(true)} onMouseLeave={(()=>setIsShiny(false))}>
                <img src={getPokemonImage()}/>
                <h3>{pokemon.name}</h3>
            </div>
        }
        </>
    );
}

export default Pokemon;
