import React, { useEffect, useState } from 'react';

function Login() {

    const [mail, setMail] = useState(""); // Déclaration du state
    const [password, setPassword] = useState("");

    const [buttonTitle, setButtonTitle] = useState("OK");

    useEffect(()=>{ // Equivaut au componentDidMount : s'execute à chaque modification de buttonTitle
        setButtonTitle("Valider");// Assigner une nouvelle valeur au buttonTitle
    },[buttonTitle]); 

    function changeMail(e){ // Modifie la valeur du state par celle entrée dans l'input mail
        setMail(e.target.value);
    }

    function changePassword(event){ // Modifie la valeur du state par celle entrée dans l'input password
        setPassword(event.target.value);
    }

    function valid(){ // Vérifie les infos de connexion lors du click sur le bouton
        if (mail == "test" && password == "1234"){
            alert("Connecté");
        } else { 
            alert("try again");
        }     
    }

    return (
        <div>
            <input onChange={(e)=>changeMail(e)} placeholder="Entrer le mail" value={mail}></input>
            <input onChange={(event)=>changePassword(event)} type="password" placeholder="Entrer le mail" value={password}></input>
            <button onClick={()=>valid()}>{buttonTitle}</button>
        </div>
    );
}

export default Login;
